import React, { useEffect, useState } from 'react';

const ImageSection = () => {
  const [latestImage, setLatestImage] = useState(null);
  const [analyzedImage, setAnalyzedImage] = useState(null);
  const [latestAudio, setLatestAudio] = useState(null);
  const [analyzedAudio, setAnalyzedAudio] = useState(null);
  const [predictionText, setPredictionText] = useState("");

  useEffect(() => {
    const fetchPrediction = async (filename) => {
      const txtName = filename.replace(".jpg", ".txt");
      try {
        // const res = await fetch(`http://220.149.235.221:4000/save_bird_class/${txtName}`);
        const res = await fetch(`http://220.149.235.221:4000/images_result/${txtName}`);

        const text = await res.text();

        // ÏùëÎãµÏù¥ HTML ÌòïÏãù(ÏóêÎü¨ ÌéòÏù¥ÏßÄ)Ïù¥Î©¥ Î¨¥Ïãú
        if (text.startsWith('<!doctype html>')) {
          console.warn("‚ùó Ïú†Ìö®ÌïòÏßÄ ÏïäÏùÄ ÏòàÏ∏° Í≤∞Í≥º (404 ÎòêÎäî HTML ÏóêÎü¨ ÌéòÏù¥ÏßÄ)");
          setPredictionText("");  // ÏòàÏ∏° ÌÖçÏä§Ìä∏ Ï¥àÍ∏∞Ìôî
        } else {
          setPredictionText(text.trim());
        }
      } catch (err) {
        console.error("‚ùå ÏòàÏ∏° Í≤∞Í≥º Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", err);
        setPredictionText("");
      }
    };

    const fetchAssets = async () => {
      try {
        // Ïù¥ÎØ∏ÏßÄ
        const imageRes = await fetch('http://220.149.235.221:4000/api/images');
        const imageList = await imageRes.json();
        const latestImg = imageList
          .filter(name => name.startsWith('frame_') && name.endsWith('.jpg'))
          .sort((a, b) => parseInt(b.match(/frame_(\d+)/)?.[1] || 0) - parseInt(a.match(/frame_(\d+)/)?.[1] || 0))[0];
        if (latestImg) {
          setLatestImage(`http://220.149.235.221:4000/images/${latestImg}`);
          await fetchPrediction(latestImg);
        } else {
          setLatestImage(null);
          setPredictionText("");
        }

        // Î∂ÑÏÑù Ïù¥ÎØ∏ÏßÄ
        const resultRes = await fetch('http://220.149.235.221:4000/api/images_result');
        const resultList = await resultRes.json();
        const latestResult = resultList
          .filter(name => name.startsWith('frame_result_') && name.endsWith('.jpg'))
          .sort((a, b) => parseInt(b.match(/frame_result_(\d+)/)?.[1] || 0) - parseInt(a.match(/frame_result_(\d+)/)?.[1] || 0))[0];
        // setAnalyzedImage(latestResult ? `http://220.149.235.221:4000/images_result/${latestResult}` : null);
        if (latestResult) {
          setAnalyzedImage(`http://220.149.235.221:4000/images_result/${latestResult}`);
          await fetchPrediction(latestResult); // Ï∂îÍ∞ÄÎêú Î∂ÄÎ∂Ñ
        } else {
          setAnalyzedImage(null);
        }


        // Ïò§ÎîîÏò§
        const audioRes = await fetch('http://220.149.235.221:4000/api/sounds');
        const audioList = await audioRes.json();
        const original = audioList
          .filter(name => !name.includes('_result'))
          .sort((a, b) => parseInt(b.match(/(\d+)/)?.[1] || 0) - parseInt(a.match(/(\d+)/)?.[1] || 0))[0];
        const analyzed = original?.replace(/\.(wav|mp3)$/, '_result.$1');
        setLatestAudio(original ? `http://220.149.235.221:4000/sounds/${original}` : null);
        setAnalyzedAudio(analyzed ? `http://220.149.235.221:4000/sounds/${analyzed}` : null);
      } catch (err) {
        console.error("‚ùå Ï†ÑÏ≤¥ Î¶¨ÏÜåÏä§ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", err);
        setLatestImage(null);
        setAnalyzedImage(null);
        setLatestAudio(null);
        setAnalyzedAudio(null);
        setPredictionText("");
      }
    };

    fetchAssets();
    const interval = setInterval(fetchAssets, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="p-8 grid grid-rows-1 gap-5">
      {/* Ïù¥ÎØ∏ÏßÄ ÏòÅÏó≠ */}
      <div className="grid grid-cols-2 gap-6">
        <div>
          <div className="text-center font-bold text-lg mb-3">ÏµúÍ∑ºÏóê Ï∞çÌûå ÏÇ¨ÏßÑ</div>
          <div className="border border-black h-72 flex items-center justify-center">
            {latestImage ? (
              <img src={latestImage} alt="ÏµúÍ∑º Ïù¥ÎØ∏ÏßÄ" className="max-h-full object-contain" />
            ) : (
              <span className="text-gray-500">Ïù¥ÎØ∏ÏßÄÎ•º Î∂àÎü¨Ïò§Îäî Ï§ë...</span>
            )}
          </div>
        </div>
        <div>
          <div className="text-center font-bold text-lg mb-3">AIÍ∞Ä Î∂ÑÏÑùÌïú ÏÇ¨ÏßÑ</div>
          <div className="border border-black h-72 flex items-center justify-center">
            {analyzedImage ? (
              <img src={analyzedImage} alt="AI Î∂ÑÏÑù Ïù¥ÎØ∏ÏßÄ" className="max-h-full object-contain" />
            ) : (
              <span className="text-gray-500">Î∂ÑÏÑù Ïù¥ÎØ∏ÏßÄÎ•º Î∂àÎü¨Ïò§Îäî Ï§ë...</span>
            )}
          </div>
        </div>
      </div>

      {/* ÏòàÏ∏°Îêú ÏÉà Ïù¥Î¶Ñ */}
      {predictionText && (
        <div className="text-center text-2xl font-semibold text-blue-700 my-1 leading-tight h-[80px]">
          <div>üö® <span className="font-bold underline">{predictionText}</span> Í∞Ä Ïπ¥Î©îÎùºÏóê ÎÇòÌÉÄÎÇ¨Îã§!</div>
          <div>üì¢ {predictionText}Í∞Ä Ïã´Ïñ¥ÌïòÎäî ÏÜåÎ¶¨Î°ú Ï´ìÎäî Ï§ë...</div>
        </div>
      )}

      {/* Ïò§ÎîîÏò§ ÏòÅÏó≠ */}
      <div className="grid grid-cols-2 gap-6">
        <div>
          <div className="text-center font-bold text-lg mb-3">ÏµúÍ∑ºÏóê ÎÖπÏùåÎêú ÏùåÏÑ±</div>
          <div className="border border-black h-36 flex items-center justify-center">
            {latestAudio ? (
              <audio controls>
                <source src={latestAudio} />
                Î∏åÎùºÏö∞Ï†ÄÍ∞Ä Ïò§ÎîîÏò§ ÌÉúÍ∑∏Î•º ÏßÄÏõêÌïòÏßÄ ÏïäÏäµÎãàÎã§.
              </audio>
            ) : (
              <span className="text-gray-500">Ïò§ÎîîÏò§Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</span>
            )}
          </div>
        </div>
        <div>
          <div className="text-center font-bold text-lg mb-3">AIÍ∞Ä Î∂ÑÏÑùÌïú ÏùåÏÑ±</div>
          <div className="border border-black h-36 flex items-center justify-center">
            {analyzedAudio ? (
              <audio controls>
                <source src={analyzedAudio} />
                Î∏åÎùºÏö∞Ï†ÄÍ∞Ä Ïò§ÎîîÏò§ ÌÉúÍ∑∏Î•º ÏßÄÏõêÌïòÏßÄ ÏïäÏäµÎãàÎã§.
              </audio>
            ) : (
              <span className="text-gray-500">Î∂ÑÏÑù Ïò§ÎîîÏò§Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</span>
            )}
          </div>
            {predictionText && (
              <div className="text-center text-2xl font-semibold text-blue-700 my-1 leading-tight h-[80px]">
                <div>üö® <span className="font-bold underline">{predictionText}</span> Í∞Ä ÎßàÏù¥ÌÅ¨Ïóê ÎÇòÌÉÄÎÇ¨Îã§!</div>
                <div>üì¢ {predictionText}Í∞Ä Ïã´Ïñ¥ÌïòÎäî ÏÜåÎ¶¨Î°ú Ï´ìÎäî Ï§ë...</div>
              </div>
            )}
        </div>
      </div>
    </div>
  );
};

export default ImageSection;
